
function findRaderMapFun() {
	var myChart = echarts.init(document.getElementById("rpt6"));
	var option = {
			
		backgroundColor: '#ffffff',
		title: {},
    	tooltip: {
      		position: function(p){
      			//（数据展示图）鼠标右下角显示
      			return [p[0] + 10, p[1] - 10];
      		}
    	},
    	legend:{
    		show:true,
    		icon:'rect',
    		top:'40%',
    		left:'15%',
    		itemHeight:20,
    		itemWidth:20,
    		itemGap:30,
    		orient:'vertical',
    		data:[],
    	},
	    polar: [{
	    	name: {
	    		formatter:radarMapIndicator,
	    		textStyle:{color:'#000000'}
	    	},
	    	indicator : radarMapIndicator,
	    	center: ['50%', '50%'],
	    	radius: 70	    	
	    }],
	    //数值序列
	    series: [{
	      type: 'radar',
	      avoidLabelOverlap: false,
	      data: [{
			value: params,
			areaStyle:{
				type: ''
			}}]
	    }]
	};
  	myChart.setOption(option);
  	
  	//使用刚指定的配置项和数据展示
  	myChart.on("legendselectchanged", function(params){
  		myChart.setOption(option,true);
  	}); 
  	
}